import React, { Component } from 'react';
import { MyContext } from '../../context';
import { Button, Card, Avatar, Descriptions } from 'antd';
import Nav from '../home/Nav'
import EditProfile from '../Modal/EditProfile'

const { Meta } = Card

export default class Profile extends Component {
  state = {
    user: {},
    visible: false
  }

  componentDidMount() {
    if (!this.context.state.loggedUser) return this.props.history.push('/login');
  }

  logOut = () => {
    this.props.history.push('/login')
    this.context.logOut()
  }

  showModal = () => {
    this.setState({
      visible: true,
    });
  };

  render() {
    if (!this.context.state.loggedUser) return <p>Loading data...</p>
    const { name, email, gender, phone, image } = this.context.state.loggedUser
    return (
      <>
        <Nav></Nav>
        <Card>
          <Meta
            avatar={<Avatar size={130} src={image} />}
            title={name} />
          <Descriptions>
            <Descriptions.Item>email: {email}</Descriptions.Item>
            <Descriptions.Item>genero: {gender}</Descriptions.Item>
            <Descriptions.Item>Telefono:{phone}</Descriptions.Item>
          </Descriptions>
          <Button htmlType="submit" onClick={this.logOut}>Log out</Button>
          <Button type="primary" onClick={this.showModal}>
            Edit Profile
        </Button>
          <EditProfile></EditProfile>
        </Card>
      </>
    );
  }
}

Profile.contextType = MyContext;
