import React, { Component } from 'react'
import { Modal, Form } from 'antd';
import { MyContext } from '../../context';


export default class EditProfile extends Component {
  state = {
    user: {}
  }

  render() {
    console.log(this.state.user)
    const { name, email, phone } = this.context.state.loggedUser
    return (
      <div>
        <Modal
          title="Edit Profile"
          visible={this.props.visible}
          onOk={this.props.handleOk}
          onCancel={this.props.handleCancel}
        >
          <Form onSubmit={this.props.onSubmit} style={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center'
          }}>

            <input name="name" onChange={this.props.handleInput} defaultValue={name}></input>

            <input name="email" onChange={this.props.handleInput} defaultValue={email}></input>

            <input type="number" min="0" maxLength="10" name="phone" onChange={this.props.handleInput} defaultValue={phone}></input>
          </Form>
        </Modal>
      </div>
    )
  }
}

EditProfile.contextType = MyContext
