import React, { Component } from 'react'
import MY_SERVICE from '../../services'
import { Card, Form, Input } from 'antd'
import { Link } from 'react-router-dom'

export default class Login extends Component {
  state = {
    user: {}
  }

  onSubmit = (e) => {
    e.preventDefault();
    MY_SERVICE.login(this.state.user)
      .then((response) => {
        this.context.logUser(response.data.user);
        this.props.history.push('/');
      })
      .catch((error) => {
        console.log(error);
      });
  };

  onSubmit = (e) => {
    e.preventDefault()
    MY_SERVICE.signup(this.state.user)
      .then((response) => console.log(response.data))
      .catch((err) => console.log(err))
  }

  render() {
    return (
      <div
        style={{
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          flexDirection: 'column',
          width: '100vw',
          height: '100vh'
        }}>
        <Card style={{ width: '50vw' }} title='Signup'>
          <Form onSubmit={this.onSubmit}>

            <Form.Item label="Email">
              <Input name="email" type="text" onChange={this.handeleInput} />
            </Form.Item>

            <Form.Item type="password" label="Password">
              <Input onChange={this.handeleInput} />
            </Form.Item>

            <Form.Item type="password">
              <Input name="password" value="Signup" type="submit" />
              <Link to="./signup">
                Create an account
              </Link>
            </Form.Item>
          </Form>
        </Card>
      </div>
    )
  }
}
