import React, { Component } from 'react'
import { Modal } from 'antd';
import { MyContext } from '../../context';


export default class EditProfile extends Component {
  state = {
    user: {}
  }
  handleInput = (e) => {
    const { user } = this.state
    const key = e.target.name
    user[key] = e.target.value
    this.setState({ user })
  }

  handleChange = (e) => {
    this.setState({ value: e.target.value })
  }

  render() {
    const { name, email, gender, phone } = this.context.state.loggedUser
    return (
      <div>
        <Modal
          title="Edit Profile"
          visible={this.props.visible}
          onOk={this.props.handleOk}
          onCancel={this.props.handleCancel}
        >
          <form onSubmit={this.onSubmit} style={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center'
          }}>

            <input onChange={this.handleInput} name={name}></input>


            <input onChange={this.handleinput} name={email}></input>

            <select name={gender}>
              <option value="Male">Male</option>
              <option value="Female">Female</option>
            </select>

            <input onChange={this.handleinput} name={phone}></input>
          </form>
        </Modal>
      </div>
    )
  }
}

EditProfile.contextType = MyContext
