import React, { Component } from 'react'
import { Modal } from 'antd';
import { MyContext } from '../../context';

const { Option } = Select

export default class EditProfile extends Component {

  // handleInput = (e) => {
  //   const { user } = this.state
  //   const key = e.target.name
  //   user[key] = e.target.value
  //   this.setState({ user })
  // }

  // handleChange = (e) => {
  //   this.setState({ value: e.target.value })
  // }

  render() {
    const { name, email, gender, phone } = this.context.state.loggedUser
    return (
      <div>
        <Modal
          title="Edit Profile"
          visible={this.props.visible}
          onOk={this.props.handleOk}
          onCancel={this.props.handleCancel}
        >
          <Form onSubmit={this.onSubmit}>
            <Form.Item label="User name">
              <Input onChange={this.handleInput} value={name}></Input>
            </Form.Item>
            <Form.Item label="Email">
              <Input onChange={this.handleInput}>{email}</Input>
            </Form.Item>
            <Form.Item label="Gender">
              <Select value={gender}>
                <Option value="Male">Male</Option>
                <Option value="Female">Female</Option>
              </Select>
            </Form.Item>
            <Form.Item label="Phone">
              <Input onChange={this.handleInput}>{phone}</Input>
            </Form.Item>
          </Form>
        </Modal>
      </div>
    )
  }
}

EditProfile.contextType = MyContext
