import React, { Component } from 'react'
import axios from 'axios'
import PLACE_SERVICE from '../../services/placeService'
import Nav from '../home/Nav'
import { Card } from 'antd'

export default class Dashboard extends Component {
  state = {
    user: {},
    places: []
  }
  //estado de logeo
  componentDidMount() {
    if (localStorage.user) {
      let user = JSON.parse(localStorage.user)
      this.setState({ user })
      const { placeID } = this.props
      PLACE_SERVICE.showAllPlace(placeID)
        .then(({ data }) => {
          if (data.places) {
            this.setState({ places: [...data.places] });
          }
        })
        .catch((err) => console.log('este es mi error', err))

    }
    if (!localStorage.user) {
      return this.props.history.push('/login')
    }
  }

  logOut = async () => {
    await axios.get('http://localhost:3000/api/logout')
    localStorage.removeItem('user')
    this.props.history.push('/login')
  }

  render() {
    const { places } = this.state
    return (
      <div
        style={{
          display: 'flex',
          alignItems: 'center',
          flexDirection: 'column',
          width: '100vw',
          height: '100vh'
        }}>
        <Nav logOut={this.logOut}></Nav>
        {places.map((place) => (
          <Card title={place.name} style={{ width: '25%' }}>

          </Card>
        ))}
      </div>
    )
  }
}
