import React, { Component } from 'react'
import axios from 'axios'
import PLACE_SERVICE from '../../services/placeService'
import Nav from '../home/Nav'
import { Card } from 'antd'
import moment from 'moment'

export default class Dashboard extends Component {
  state = {
    places: []
  }

  componentDidMount() {
    PLACE_SERVICE.showAllPlace()
      .then(({ data }) => {
        if (data) this.setState({ places: [...data] });
      })
      .catch((err) => console.log('este es mi error', err))
  }

  logOut = async () => {
    await axios.get('http://localhost:3000/api/logout')
    localStorage.removeItem('user')
    this.props.history.push('/login')
  }

  render() {
    console.log('estos son los props', this.props)
    console.log('este el state', this.state)
    const { places } = this.state
    return (
      <section
        style={{
          display: 'flex',
          alignItems: 'center',
          flexDirection: 'column',
          width: '100vw',
          height: '100vh'
        }}>
        <Nav logOut={this.logOut}></Nav>
        <div
          style={{
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'space-evenly',
            flexWrap: 'wrap',
            width: '70vw',
            height: '100vh'
          }}>
          {places.map((place) => (
            <Card key="loquesea" title={place.owner.name} style={{ width: '33%', height: '30vh' }}>
              <div>
                <p key="suburb">{place.suburb}</p>
                <p key="delegation">{place.delegation},</p>
                <p key="country"> {place.country}</p>
                <p key="desc">{place.description}</p>
                <p key="service">{place.services}</p>
                <p key="rules">{place.rules}</p>
                {
                  place.ocupationDate && place.evictionDate ?
                    (<p><b key="initialDate">{moment(place.ocupationDate).format('L')}</b> - <b key="endDate">{moment(place.evictionDate).format('L')}</b></p>) :
                    (<p></p>)
                }
              </div>
            </Card>
          ))}
        </div>
      </section>
    )
  }
}
