import React, { Component } from 'react'
import { Modal, Form, Input } from 'antd';
import { MyContext } from '../../context';


export default class EditProfile extends Component {
  state = {
    user: {}
  }

  render() {
    const { name, email, phone } = this.context.state.loggedUser
    return (
      <div>
        <Modal
          title="Edit Profile"
          visible={this.props.visible}
          onOk={this.props.handleOk}
          onCancel={this.props.handleCancel}
        >
          <Form onSubmit={this.onSubmit} style={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center'
          }}>

            <Input name="name" onChange={this.handleInput} value={name}></Input>


            <Input name="email" onChange={this.handleInput} value={email}></Input>

            <select name="gender">
              <option value="Male">Male</option>
              <option value="Female">Female</option>
            </select>

            <Input name="phone" onChange={this.handleInput} value={phone}></Input>
          </Form>
        </Modal>
      </div>
    )
  }
}

EditProfile.contextType = MyContext
