import React, { Component } from 'react'
import axios from 'axios'
import PLACE_SERVICE from '../../services/placeService'
import Nav from '../home/Nav'
import { Card } from 'antd'
import moment from 'moment'

export default class Dashboard extends Component {
  state = {
    places: []
  }

  componentDidMount() {
    PLACE_SERVICE.showAllPlace()
      .then(({ data }) => {
        this.setState({ places: [...data.places] });
      })
      .catch((err) => console.log('este es mi error', err))
  }

  logOut = async () => {
    await axios.get('http://localhost:3000/api/logout')
    localStorage.removeItem('user')
    this.props.history.push('/login')
  }

  render() {
    console.log('estos son los props', this.props)
    console.log('este el state', this.state)
    const { places } = this.state
    return (
      <div
        style={{
          display: 'flex',
          alignItems: 'center',
          flexDirection: 'column',
          width: '100vw',
          height: '100vh'
        }}>
        <Nav logOut={this.logOut}></Nav>

        {places.map((place) => (
          <Card title={place.name} style={{ width: '25%' }}>
            <div>
              <p>{place.suburb}, {place.delegation}, {place.country}</p>
              <p>{place.description}</p>
              <p>{place.services}</p>
              <p>{place.rules}</p>
              {
                place.ocupationDate && place.evictionDate ?
                  (<p>{moment(place.ocupationDate).format('L')} - {moment(place.evictionDate).format('L')}</p>) :
                  (<p></p>)
              }
            </div>
          </Card>
        ))}

      </div>
    )
  }
}
