import React, { Component } from 'react';
import { MyContext } from '../../context';
import { Button, Card, Avatar } from 'antd';

const { Meta } = Card

export default class Profile extends Component {
  state = {
    user: {}
  }

  componentDidMount() {
    if (!this.context.state.loggedUser) return this.props.history.push('/login');
  }

  logOut = () => {
    this.props.history.push('/login')
    this.context.logOut()
  }

  render() {
    if (!this.context.state.loggedUser) return <p>Loading data...</p>
    const { name, email, gender, phone, image } = this.context.state.loggedUser
    return (
      <Card
        style={{
          display: 'flex'
        }}>
        <Meta
          avatar={<Avatar size={130} src={image} />}
          title={name} />
        <p>email: {email}</p>
        <p>genero: {gender}</p>
        <p>Telefono:{phone}</p>
        <Button htmlType="submit" onClick={this.logOut}>Log out</Button>
      </Card>
    );
  }
}

Profile.contextType = MyContext;
