import React, { Component } from 'react';
import { MyContext } from '../../context';
import { Button, Card, Avatar, Descriptions } from 'antd';
import Nav from '../home/Nav'
import EditProfile from '../Modal/EditProfile'
import axios from 'axios'

const { Meta } = Card

export default class Profile extends Component {
  state = {
    user: {
      name: '',
      email: '',
      phone: ''
    },
    visible: false,
  }

  onSubmit = (e) => {
    e.preventDefault()
    const { user } = this.state
    axios.put(`http://localhost:3000/api/auth/profile/${user.id}`, user)
      .then(({ data }) => {
        console.log(data);
        this.setState({
          user: {
            name: '',
            email: '',
            phone: ''
          }
        });
        this.props.history.push('/profile')
      })
      .catch((error) => {
        console.log(error);
      })
  }

  componentDidMount() {
    if (!this.context.state.loggedUser) return this.props.history.push('/login');
  }

  logOut = () => {
    this.props.history.push('/login')
    this.context.logOut()
  }

  handleInput = (e) => {
    const { user } = this.state
    const key = e.target.name
    user[key] = e.target.value
    console.log(key)
    this.setState({ user })
  }

  handleChange = (e) => {
    this.setState({ value: e.target.value })
  }

  handleOk = e => {
    console.log(e);
    this.onSubmit()
    this.setState({
      visible: false,
    });
  };

  handleCancel = e => {
    console.log(e);
    this.setState({
      visible: false,
    });
  };

  showModal = () => {
    this.setState({
      visible: true,
    });
  };

  render() {
    if (!this.context.state.loggedUser) return <p>Loading data...</p>
    const { name, email, phone, image } = this.context.state.loggedUser
    return (
      <div style={{
        display: 'flex',
        alignItems: 'center',
        flexDirection: 'column',
        width: '100vw',
        height: '100vh'
      }}>
        <Nav logOut={this.logOut}></Nav>
        <Card style={{ width: "70vw" }}>
          <Meta
            avatar={<Avatar size={130} src={image} />}
            title={name}
            description={
              <Descriptions>
                <br />
                <br />
                <Descriptions.Item>Email: {email}</Descriptions.Item>
                <Descriptions.Item>Telefono:{phone}</Descriptions.Item>
              </Descriptions>
            }
          />
          <Button type="primary" onClick={this.showModal}>Edit Profile</Button>
          <EditProfile
            onSubmit={this.onSubmit}
            handleChange={this.handleChange}
            handleInput={this.handleInput}
            showModal={this.showModal}
            visible={this.state.visible}
            handleOk={this.handleOk}
            handleCancel={this.handleCancel}
          />
        </Card>
      </div>
    );
  }
}

Profile.contextType = MyContext;
