import React, { Component } from 'react'
import { Modal, Form, Input, DatePicker, Button } from 'antd'

const { RangePicker } = DatePicker

class ModalPlace extends Component {

  //Datpicker and timepicker methods


  handleDate = e => {
    e.preventDefault()
    return this.props.form.validateFields((err, fieldsValue) => {
      fieldsValue['range-picker'] = [rangeValue[0].format('YYYY-MM-DD'), rangeValue[1].format('YYYY-MM-DD')]
      return console.log(fieldsValue)
    })
  }
  // handleDate = (e) => {
  //   console.log('VALUES', e)
  //   e.preventDefault()
  //   this.props.form.validateFields((err, fieldsValue) => {
  //     if (err) return err
  //     //fromato de la fecha para antes de enviar
  //     const rangeValue = fieldsValue['range-picker']
  //     const values = {
  //       ...fieldsValue,
  //       'range-picker': [rangeValue[0].format('YYYY-MM-DD'), rangeValue[1].format('YYYY-MM-DD')]
  //     }
  //     console.log('Valores recibidos del datePicker: ', values);
  //     this.props.onSubmit()
  //     this.props.form.resetFields()
  //   })
  // }


  render() {
    const { visible, onCancel, handleCancel, handleInput, onChangeDate } = this.props
    const { getFieldDecorator } = this.props.form;
    const rangeConfig = {
      rules: [{ type: 'array', required: true, message: 'Selecciona una fecha!' }],
    }
    return (
      <div>
        <Modal
          title="Set your place!"
          visible={visible}
          footer={null}
          header={null}
          onCancel={onCancel}>
          <Form onReset={handleCancel} onSubmit={this.handleDate}>
            <Form.Item label="Colonia">
              {getFieldDecorator('suburb')(<Input name="suburb" />)}
            </Form.Item>
            <Form.Item label="Delegacion">
              {getFieldDecorator('delegation')(<Input name="delegation" />)}
            </Form.Item>
            <Form.Item label="Estado">
              {getFieldDecorator('country')(<Input name="country" />)}
            </Form.Item>
            <Form.Item label="Descrpición">
              {getFieldDecorator('description')(<Input name="description" />)}
            </Form.Item>
            <Form.Item label="Servicios">
              {getFieldDecorator('services')(<Input name="services" />)}
            </Form.Item>
            <Form.Item label="Reglamento">
              {
                getFieldDecorator('rules')(<Input name="rules" />)
              }
            </Form.Item>
            <Form.Item label="Fechas de ocupacion y desalojo">
              {getFieldDecorator('range-picker', rangeConfig)(<RangePicker />)}
            </Form.Item>
            <Form.Item>
              <Button type="primary" htmlType="submit" >SAVE</Button>
              <Button htmlType="reset" >Cancel</Button>
            </Form.Item>
          </Form>
        </Modal >
      </div >
    )
  }
}

const FormPlace = Form.create()(ModalPlace);
export default FormPlace
